// Smart agent assignment algorithm
const escalations = $input.first().json.records || [];

// Define support team (would be from config/database in production)
const supportTeam = [
  {
    id: 'agent_1',
    name: 'Sarah Chen',
    email: 'sarah@company.com',
    slack_id: 'U01ABC123',
    specialties: ['BUG', 'FEATURE'],
    max_tickets: 10,
    priority_preference: ['URGENT', 'HIGH'],
    current_load: 3,
    availability: 'available'
  },
  {
    id: 'agent_2',
    name: 'Mike Johnson',
    email: 'mike@company.com',
    slack_id: 'U02DEF456',
    specialties: ['BILLING', 'GENERAL'],
    max_tickets: 8,
    priority_preference: ['URGENT', 'HIGH', 'MEDIUM'],
    current_load: 2,
    availability: 'available'
  },
  {
    id: 'agent_3',
    name: 'Emily Rodriguez',
    email: 'emily@company.com',
    slack_id: 'U03GHI789',
    specialties: ['BUG', 'BILLING'],
    max_tickets: 12,
    priority_preference: ['HIGH', 'MEDIUM'],
    current_load: 5,
    availability: 'available'
  },
  {
    id: 'agent_4',
    name: 'David Kim',
    email: 'david@company.com',
    slack_id: 'U04JKL012',
    specialties: ['FEATURE', 'GENERAL'],
    max_tickets: 10,
    priority_preference: ['URGENT', 'HIGH'],
    current_load: 4,
    availability: 'available'
  }
];

// Assignment algorithm
function assignAgent(ticket) {
  const category = ticket.fields?.Category || 'GENERAL';
  const priority = ticket.fields?.Priority || 'MEDIUM';

  // Filter available agents
  let availableAgents = supportTeam.filter(agent => 
    agent.availability === 'available' && 
    agent.current_load < agent.max_tickets
  );

  if (availableAgents.length === 0) {
    // All at capacity - assign to least loaded
    availableAgents = supportTeam.sort((a, b) => a.current_load - b.current_load);
  }

  // Score agents based on specialty and workload
  const scoredAgents = availableAgents.map(agent => {
    let score = 0;

    // Specialty match (highest priority)
    if (agent.specialties.includes(category)) {
      score += 50;
    }

    // Priority preference
    if (agent.priority_preference.includes(priority)) {
      score += 30;
    }

    // Workload balance (lower load = higher score)
    score += (agent.max_tickets - agent.current_load) * 2;

    // Urgent tickets to agents who prefer them
    if (priority === 'URGENT' && agent.priority_preference[0] === 'URGENT') {
      score += 20;
    }

    return { ...agent, score };
  });

  // Sort by score (highest first)
  scoredAgents.sort((a, b) => b.score - a.score);

  // Return best match
  return scoredAgents[0];
}

// Assign all unassigned escalations
const assignments = escalations.map(escalation => {
  const assignedAgent = assignAgent(escalation);

  // Increment agent's load
  const agentIndex = supportTeam.findIndex(a => a.id === assignedAgent.id);
  if (agentIndex !== -1) {
    supportTeam[agentIndex].current_load++;
  }

  return {
    record_id: escalation.id,
    ticket_id: escalation.fields?.['Ticket ID'],
    category: escalation.fields?.Category,
    priority: escalation.fields?.Priority,
    sentiment: escalation.fields?.Sentiment,
    customer_email: escalation.fields?.['Customer Email'],
    message: escalation.fields?.Message,
    escalation_reason: escalation.fields?.['Escalation Reason'],
    created: escalation.fields?.Created,
    assigned_agent: {
      id: assignedAgent.id,
      name: assignedAgent.name,
      email: assignedAgent.email,
      slack_id: assignedAgent.slack_id
    },
    assignment_score: assignedAgent.score,
    assignment_time: new Date().toISOString()
  };
});

return assignments.map(a => ({ json: a }));

