// Calculate SLA and overdue status
const tickets = $input.first().json.records || [];

// SLA targets (in hours)
const slaTargets = {
  URGENT: 1,
  HIGH: 4,
  MEDIUM: 24,
  LOW: 48
};

const overdueTickets = tickets.map(ticket => {
  const priority = ticket.fields?.Priority || 'MEDIUM';
  const assignedAt = new Date(ticket.fields?.['Assigned At'] || ticket.fields?.Created);
  const now = new Date();

  const hoursOpen = (now - assignedAt) / (1000 * 60 * 60);
  const slaTarget = slaTargets[priority] || 24;
  const hoursOverdue = hoursOpen - slaTarget;
  const percentOverdue = (hoursOverdue / slaTarget * 100).toFixed(0);

  // Only include if actually overdue
  if (hoursOverdue <= 0) {
    return null;
  }

  return {
    record_id: ticket.id,
    ticket_id: ticket.fields?.['Ticket ID'],
    priority,
    assigned_to: ticket.fields?.['Assigned To'],
    assigned_email: ticket.fields?.['Assigned Email'],
    customer_email: ticket.fields?.['Customer Email'],
    category: ticket.fields?.Category,
    message: ticket.fields?.Message,
    assigned_at: assignedAt.toISOString(),
    hours_open: hoursOpen.toFixed(1),
    sla_target: slaTarget,
    hours_overdue: hoursOverdue.toFixed(1),
    percent_overdue: percentOverdue,
    severity: hoursOverdue > slaTarget * 2 ? 'CRITICAL' : 
              hoursOverdue > slaTarget ? 'HIGH' : 'MEDIUM'
  };
}).filter(t => t !== null);

return overdueTickets.map(t => ({ json: t }));

